using System;
using System.Collections.Generic;
using System.Text;
using System.IO;
using System.Threading;
using System.IO.Compression;

namespace Multithreading
{
    /// <summary>
    /// Класс описания задачи сжатия
    /// </summary>
	public class CompressionTaskInfo : ITaskInfo
    {
        private Int64 _id;
        private byte[] _bytes;

		public CompressionTaskInfo(Int64 id, byte[] bytes)
        { 
            _id = id;
            _bytes = bytes;
		}

        public object Execute(object obj)
        {
            using (MemoryStream memoryStream = new MemoryStream())
            {
                using (GZipStream compressStream = new GZipStream(memoryStream, CompressionLevel.Optimal))
                {
                    compressStream.Write(_bytes, 0, _bytes.Length);
                }
                return memoryStream;
            }
        }

        public object GetData()
        {
            return _bytes;
        }

        public Int64 GetId()
        {
            return _id;
        }
    }
}